import React,{useContext} from 'react'
import Data from './Data'
import { data_pass } from './List'
const Category = () => {

  const state = useContext(data_pass)

  const filter_item = (categ) => {
       const  item = Data.filter((element)=>{
        return  element.category === categ
       }
      
       )
       state.set_card(item)
  }

  return (
    <div className='btn_group'>
        <button className='btn_css' 
        onClick={()=>state(Data)}
        >All</button>
        <button className='btn_css' 
        onClick={()=>filter_item('Breakfast')}
        >Breakfast</button>
        <button className='btn_css' 
        onClick={()=>filter_item('Lunch')}
        >Lunch</button>
        <button className='btn_css' 
        onClick={()=>filter_item('Dinner')}
        >Dinner</button>
    </div>
  )
}

export default Category
