import React, { useContext } from 'react'
import Data from './Data'
import { Properties } from './Main'

const Category = () => {
const data = useContext(Properties)


 

  const filter_item = (categ) => {
       const  item = Data.filter((element)=>{
        return  element.category === categ
       }
      
       )
      //  setcard.setallcards(item)
  }

  return (
    <div className='btn_group'>
        <button className='btn_css' 
        onClick={()=>filter_item('Data')}
        >All</button>
        <button className='btn_css' 
        onClick={()=>filter_item('Breakfast')}
        >Breakfast</button>
        <button className='btn_css' 
        onClick={()=>filter_item('Lunch')}
        >Lunch</button>
        <button className='btn_css' 
        onClick={()=>filter_item('Dinner')}
        >Dinner</button>
    </div>
  )
}

export default Category
